import { Content } from 'mdast';
import { Node } from 'unist';
/**
 * Type guard for Content.
 *
 * @param node - The node to test.
 * @returns True if node is Content.
 */
export declare function isContent(node: Node): node is Content;
/**
 * Type assertion for Content.
 *
 * @param node - The node to test.
 */
export declare function assertContent(node: Node): asserts node is Content;
