import { DefinitionContent } from 'mdast';
import { Node } from 'unist';
/**
 * Type guard for DefinitionContent.
 *
 * @param node - The node to test.
 * @returns True if node is DefinitionContent.
 */
export declare function isDefinitionContent(node: Node): node is DefinitionContent;
/**
 * Type assertion for DefinitionContent.
 *
 * @param node - The node to test.
 */
export declare function assertDefinitionContent(node: Node): asserts node is DefinitionContent;
