import { Emphasis, PhrasingContent } from 'mdast';
import { Node } from 'unist';
/**
 * @param children - The emphasis content.
 * @returns The emphasis.
 */
export declare function createEmphasis(children?: PhrasingContent[]): Emphasis;
/**
 * Type guard for Emphasis.
 *
 * @param node - The node to test.
 * @returns True if node is Emphasis.
 */
export declare function isEmphasis(node: Node): node is Emphasis;
/**
 * Type assertion for Emphasis.
 *
 * @param node - The node to test.
 */
export declare function assertEmphasis(node: Node): asserts node is Emphasis;
