import { HTML } from 'mdast';
import { Node } from 'unist';
/**
 * @param value - The HTML content.
 * @returns The HTML.
 */
export declare function createHTML(value: string): HTML;
/**
 * Type guard for HTML.
 *
 * @param node - The node to test.
 * @returns True if node is HTML.
 */
export declare function isHTML(node: Node): node is HTML;
/**
 * Type assertion for HTML.
 *
 * @param node - The node to test.
 */
export declare function assertHTML(node: Node): asserts node is HTML;
