import { Parent } from 'mdast';
import { Node } from 'unist';
/**
 * Type guard for Parent.
 *
 * @param node - The node to test.
 * @returns True if node is Parent.
 */
export declare function isParent(node: Node): node is Parent;
/**
 * Type assertion for Parent.
 *
 * @param node - The node to test.
 */
export declare function assertParent(node: Node): asserts node is Parent;
