import { Content, Root } from 'mdast';
import { Node } from 'unist';
/**
 * @param children - The children of the root.
 * @returns The root.
 */
export declare function createRoot(children?: Content[]): Root;
/**
 * Type guard for Root.
 *
 * @param node - The node to test.
 * @returns True if node is Root.
 */
export declare function isRoot(node: Node): node is Root;
/**
 * Type assertion for Root.
 *
 * @param node - The node to test.
 */
export declare function assertRoot(node: Node): asserts node is Root;
