import { PhrasingContent, Strong } from 'mdast';
import { Node } from 'unist';
/**
 * @param children - The strong content.
 * @returns The strong.
 */
export declare function createStrong(children?: PhrasingContent[]): Strong;
/**
 * Type guard for Strong.
 *
 * @param node - The node to test.
 * @returns True if node is Strong.
 */
export declare function isStrong(node: Node): node is Strong;
/**
 * Type assertion for Strong.
 *
 * @param node - The node to test.
 */
export declare function assertStrong(node: Node): asserts node is Strong;
