import { YAML } from 'mdast';
import { Node } from 'unist';
/**
 * @param value - The content of the YAML.
 * @returns The YAML.
 */
export declare function createYAML(value: string): YAML;
/**
 * Type guard for YAML.
 *
 * @param node - The node to test.
 * @returns True if node is YAML.
 */
export declare function isYAML(node: Node): node is YAML;
/**
 * Type assertion for YAML.
 *
 * @param node - The node to test.
 */
export declare function assertYAML(node: Node): asserts node is YAML;
